"; } public function onCall(ConnectionInterface $conn, $id, $fn, array $params){ } public function onPublish(ConnectionInterface $conn, $conversation_id, $event, array $exclude, array $eligible){ $conversation_id = (string) $conversation_id; $nb_clients = $this->conversation[$conversation_id] echo "$nb_clients User(s) in conversation"; echo "Message sent to $conversation_id : $event"; // ... $message = $event; // Send data to conversation $this->conversation[$conversation_id]->broadcast($message); } public function onError(ConnectionInterface $conn, \Exception $e){ echo "An error has occurred: {$e->getMessage()}
